import React from 'react';
import {Link} from 'react-router-dom';
import {Content} from '@bloomreach/spa-sdk';
import {BrManageContentButton, BrPageContext, BrProps} from '@bloomreach/react-sdk';
export function ProductList(props: BrProps) {
  const { pagination } = props.component.getModels<PageableModels>();

  if (!pagination) {
    return null;
  }
  return (
    <div>
      {pagination.items?.map((reference, key) => <ProductListItem key={key} item={props.page.getContent(reference)!}/>)}
      <ProductListPagination {...pagination} />
    </div>
  );
}
interface ProductListItemProps {
  item: Content;
}
export function ProductListItem({item}: ProductListItemProps) {
  /*TODO: Get the title, introduction and price from the Product item */
  const {title, price, introduction} = item.getData<Product>();
  return (
    <div className="card mb-3">
      <BrManageContentButton content={item}/>
      <div className="card-body">
        { title && (
          <h2 className="card-title">
            <Link to={item.getUrl()!}>{title}</Link>
          </h2>
        ) }
        { price && <div className="card-subtitle mb-3 text-muted">{price}</div> }
        { introduction && <p className="card-text">{introduction}</p> }
      </div>
    </div>
  );
}
export function ProductListPagination(props: Pageable) {
  const page = React.useContext(BrPageContext);

  if (!page || !props.showPagination) {
    return null;
  }

  return (
    <nav aria-label="Product List Pagination">
      <ul className="pagination">
        <li className={`page-item ${props.previous ? '' : 'disabled'}`}>
          <Link to={props.previous ? page.getUrl(`?page=${props.previousPage}`) : '#'} className="page-link" aria-label="Previous">
            <span aria-hidden="true">&laquo;</span>
            <span className="sr-only">Previous</span>
          </Link>
        </li>
        { props.pageNumbersArray.map((pageNumber, key) => (
          <li key={key} className={`page-item ${pageNumber === props.currentPage ? 'active' : ''}`}>
            <Link to={page.getUrl(`?page=${pageNumber}`)} className="page-link">{pageNumber}</Link>
          </li>
        )) }
        <li className={`page-item ${props.next ? '' : 'disabled'}`}>
          <Link to={props.next ? page.getUrl(`?page=${props.nextPage}`) : '#'} className="page-link" aria-label="Next">
            <span aria-hidden="true">&raquo;</span>
            <span className="sr-only">Next</span>
          </Link>
        </li>
      </ul>
    </nav>
  );
}
